    /*
     * Adding a new producttype
     */
    function add()
    {   
        if(isset($_POST) && count($_POST) > 0)     
        {   
            $params = array(
				'name' => $this->input->post('name'),
				'description' => $this->input->post('description'),
				'menuId' => $this->input->post('menuId'),
            );
            
            $producttype_id = $this->Producttype_model->add_producttype($params);
            redirect('producttype/index');
        }
        else
        {            
            $data['_view'] = 'producttype/add';
            $this->load->view('layouts/main',$data);
        }
    }  

    /*
     * Editing a producttype
     */
    function edit($prodTypeId)
    {   
        // check if the producttype exists before trying to edit it
        $data['producttype'] = $this->Producttype_model->get_producttype($prodTypeId);
        
        if(isset($data['producttype']['prodTypeId']))
        {
            if(isset($_POST) && count($_POST) > 0)     
            {   
                $params = array(
					'name' => $this->input->post('name'),
					'description' => $this->input->post('description'),
					'menuId' => $this->input->post('menuId'),
                );

                $this->Producttype_model->update_producttype($prodTypeId,$params);            
                redirect('producttype/index');
            }
            else
            {
                $data['_view'] = 'producttype/edit';
                $this->load->view('layouts/main',$data);
            }
        }
        else
