    } 

    /*
     * Deleting producttype
     */
    function remove($prodTypeId)
    {
        $producttype = $this->Producttype_model->get_producttype($prodTypeId);

        // check if the producttype exists before trying to delete it
        if(isset($producttype['prodTypeId']))
        {
            $this->Producttype_model->delete_producttype($prodTypeId);
